package practiceprogram;

public class arithmaticoperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=125;
		int num2=24;
		int res;
		System.out.println("addition result of two numbers");
		res=num1+num2;
		System.out.println(res);
		System.out.println("subtraction result of two numbers");
		res=num1-num2;
		System.out.println(res);
		System.out.println("multipplication result of two numbers");
		res=num1*num2;
		System.out.println(res);
		System.out.println("division result of two numbers");
		res=num1/num2;
		System.out.println(res);
		
		System.out.println("division result of two numbers");
		res=num1%num2;
		System.out.println(res);
	}

}
